    ROUND(IFNULL(ifnull(if(b.type='Daily Wages',b.bsalary/30/b.shift_hour,b.bsalary/daystotal/b.shift_hour),0), 0),2) AS otrate,
	 ROUND(IFNULL(hw.othour, 0),2) AS othour,
    ROUND(IFNULL((IFNULL(ifnull(if(b.type='Daily Wages',b.bsalary/30/b.shift_hour,b.bsalary/daystotal/b.shift_hour),0), 0) * hw.othour), 0), 2) AS overtime,
    ROUND(IFNULL(ad.adv, 0), 2) AS 'advance',
    ROUND(IFNULL(l.deduction, 0), 2) AS 'loan_deduction',
    ROUND(IFNULL(l.balance, 0), 2) AS 'balance',
    ROUND(IFNULL(inc.inc, 0), 2) AS 'incentive',
    ROUND(IFNULL(p.pen, 0) + IFNULL(late_ded.late_ded,0) , 2) AS 'penalty',
    ROUND(IFNULL(b.eobi, 0), 2) AS 'eobi',
    ROUND(IFNULL(b.insurance, 0), 2) AS 'insurance',
    ROUND(IFNULL(b.socialsec, 0), 2) AS 'socialsec',
    -- ifnull(ROUND(((((if(b.type='Daily Wages', ROUND(b.bsalary/30, 2),b.bsalary / daystotal)) * (a.present + a.restday + paidleave + a.gustedholiday + a.outdoor + (a.shortleave / 2)))) + IFNULL((ifnull(if(b.type='Daily Wages',b.bsalary/30/b.shift_hour,b.bsalary/daystotal/b.shift_hour),0) * hw.othour), 0) + IFNULL(inc.inc, 0) - IFNULL(ad.adv, 0) - IFNULL(l.deduction, 0) - IFNULL(b.eobi, 0) - IFNULL(b.insurance, 0) - IFNULL(b.socialsec, 0) - IFNULL(p.pen, 0)),
       --     2),0) AS 'net_salary'
       
       ifnull(ROUND((((ifnull(b.bsalary,0) - ifnull(ROUND((b.bsalary / daystotal) * a.absent , 2),0))) + IFNULL((ifnull(if(b.type='Daily Wages',b.bsalary/30/b.shift_hour,b.bsalary/daystotal/b.shift_hour),0) * hw.othour), 0) + IFNULL(inc.inc, 0) - IFNULL(ad.adv, 0) - IFNULL(l.deduction, 0) - IFNULL(b.eobi, 0) - IFNULL(b.insurance, 0) - IFNULL(b.socialsec, 0) - IFNULL(p.pen, 0) - IFNULL(late_ded.late_ded, 0) ),
            2),0) AS 'net_salary'
FROM
    basicstaffinf AS b
        LEFT OUTER JOIN
    atndInf AS a ON b.staid = a.staid
        LEFT OUTER JOIN
    advance AS ad ON b.pid = ad.pid
        LEFT OUTER JOIN
    penalty AS p ON b.pid = p.pid
        LEFT OUTER JOIN
    loan AS l ON b.pid = l.pid
        LEFT OUTER JOIN
    incentive AS inc ON b.pid = inc.pid
        LEFT OUTER JOIN
    hourswork AS hw ON b.staid = hw.staid
	 LEFT JOIN(
			 	SELECT staffatndetail.staid,round(IFNULL(sal.bpay,0) / 30 / ifnull(s.shift_hour,1) * (ifnull(sum(late),0)*2),0)  late_ded 
		FROM staffatndetail 
		INNER JOIN staff stf ON stf.staid = staffatndetail.staid
		INNER JOIN 	salary AS sal ON stf.staid = sal.staid 
		INNER JOIN allot_shift AS _as ON _as.gid = stf.gid 
		INNER JOIN shift AS s ON s.shid = _as.shid 
		WHERE staffatndetail.DATE BETWEEN _from AND _to AND ifnull(late,0)>0.25
		GROUP BY staffatndetail.staid
	 ) late_ded ON late_ded.staid=b.staid
	 ;
END$$

CREATE DEFINER=`root`@`localhost` PROCEDURE `spw_account_flow` (IN `startDate` VARCHAR(200), IN `endDate` VARCHAR(200), IN `pid` INT, IN `company_id` INT)  NO SQL
