CREATE DEFINER=`root`@`localhost` PROCEDURE `spw_Inventory_Summary` (IN `startDate` VARCHAR(100), IN `endDate` VARCHAR(100), IN `crit` VARCHAR(500), IN `cid` INT)  NO SQL
SELECT c.name,i.item_id,i.item_des,i.uom, ifnull(op.opqty,0) AS 'opqty', ifnull(op.opweight,0) AS 'opweight', ifnull(cl.inweight,0) AS 'inweight', ifnull(cl.outweight,0) AS 'outweight', ifnull(cl.in,0) AS 'in', ifnull(cl.out,0) AS 'out', ifnull(op.opqty,0) + ifnull(cl.in,0) + ifnull(cl.out,0) AS 'balance', ifnull(op.opweight,0) + ifnull(cl.inweight,0) + ifnull(cl.outweight,0) AS 'balanceweight', ROUND(IFNULL(lp.rate,0),2) AS cost
FROM item i
inner join category c on c.catid=i.catid
LEFT JOIN (
SELECT item_id, SUM(IF(qty <0,qty,0)) AS 'out', SUM(IF(qty>0,qty,0)) AS 'in' , SUM(IF(weight <0,weight,0)) AS 'outweight', SUM(IF(weight>0,weight,0)) AS 'inweight'
