SELECT l1.l1 ,l2.l2,l3.l3,p.pid ,l1.name leve1 ,l2.name level2 ,l3.name level3 ,p.name party_name,p.account_id,  ifnull(cl.DEBIT,0)debit ,ifnull(cl.credit,0) credit,ifnull(cl.BALANCE,0) balance ,ifnull(op.OpBALANCE,0)opbal FROM party p
