CREATE DEFINER=`root`@`localhost` PROCEDURE `Trial_Balance` (IN `startDate` VARCHAR(200), IN `endDate` VARCHAR(200), IN `company_id` INT, IN `l1_p` INT, IN `l2_p` INT, IN `l3_p` INT)  BEGIN
	DECLARE CredSUM decimal(19,2);
	DECLARE DebSUM  decimal(19,2);
      
	CREATE TEMPORARY TABLE IF NOT EXISTS TrialBalTbl (
		CTR INT NOT NULL PRIMARY KEY AUTO_INCREMENT,
		PARTY_ID VARCHAR(100),
		ACCOUNT_ID VARCHAR(100),
		PARTY_NAME VARCHAR(500),
		DEBIT DECIMAL(19,2),
		CREDIT DECIMAL(19,2),
		L1	VARCHAR(200),
            		L1NAME VARCHAR(1000),
		L2 VARCHAR(200),
		L2NAME VARCHAR(1000),
		L3 VARCHAR(200),
		L3NAME VARCHAR(1000),
		L1DebSUM decimal(19,2),
		L1CredSUM decimal(19,2),
		L2DebSUM decimal(19,2),
		L2CredSUM decimal(19,2),
		L3DebSUM decimal(19,2),
		L3CredSUM decimal(19,2),
		totalCred decimal(19,2),
		totalDeb decimal(19,2)
	);

	TRUNCATE TABLE TrialBalTbl;

	INSERT INTO TrialBalTbl
	SELECT  
		null, 
		p.pid as PARTY_ID,
		p.account_id,
		p.NAME,
		SUM(L.DEBIT)-SUM(L.CREDIT),
		0, 
		l1.L1,
		l1.`name` AS 'L1NAME',
		l2.L2, 
		l2.`name` AS 'L2NAME',
		l3.L3,
		l3.`name` AS 'L3NAME',
		0,
		0,
		0,
		0,
		0,
		0,
		0,
		0
	FROM 
		pledger AS L INNER JOIN party AS p ON L.pid = p.pid
		INNER JOIN level3 AS l3 ON l3.l3 = p.level3
		INNER JOIN level2 AS l2 ON l2.l2 = l3.l2

		INNER JOIN level1 AS l1 ON l1.l1 = l2.l1
	WHERE 
		DATE(L.date) BETWEEN startDate AND endDate AND L.company_id=company_id
	GROUP BY 
		l1.L1, l2.L2, l3.L3, p.pid,p.NAME 
	HAVING IFNULL(SUM(L.DEBIT),0)-IFNULL(SUM(L.CREDIT),0) >0 
	order by 
		p.account_id ASC;

	INSERT INTO TrialBalTbl
	SELECT  
		null, 
		P.pid as PARTY_ID, 
		P.account_id,
		P.NAME,
		0,
		SUM(L.CREDIT)-SUM(L.DEBIT), 
		l1.L1, 
		l1.NAME AS 'L1NAME',
		l2.L2, 
		l2.NAME AS 'L2NAME',
		l3.L3,
		l3.NAME AS 'L3NAME',
		0,
		0,
		0,
		0,
		0,
		0,
		0,
		0
	FROM 
		pledger AS L INNER JOIN party AS P ON L.pid = P.pid
		INNER JOIN level3 AS l3 ON l3.l3 = P.level3
		INNER JOIN level2 AS l2 ON l2.l2 = l3.l2
		INNER JOIN level1 AS l1 ON l1.l1 = l2.l1
	WHERE  
		DATE(L.date) BETWEEN startDate AND endDate AND L.company_id=company_id
	GROUP BY  
		l1.L1, l2.L2, l3.L3, P.pid,P.NAME HAVING IFNULL(SUM(L.CREDIT),0)-IFNULL(SUM(L.DEBIT),0) > 0 
	ORDER BY 
		P.account_id ASC;

	-- Update the whole level's credit and debit sums

	UPDATE TrialBalTbl as tbal 
	SET L1DebSUM = (
			SELECT SUM(pledger.DEBIT)-SUM(pledger.CREDIT) 
			FROM 
				pledger INNER JOIN party ON pledger.pid = party.pid 
						INNER JOIN level3 ON level3.l3 = party.level3 
						INNER JOIN level2 ON level3.l2 = level2.l2 
						INNER JOIN level1 on level1.l1 = level2.l1
			WHERE 
				level1.l1 = tbal.l1 AND DATE(pledger.date) BETWEEN startDate AND endDate AND pledger.company_id=company_id
			GROUP BY  
				level1.L1
			HAVING 
				IFNULL(SUM(pledger.DEBIT),0)-IFNULL(SUM(pledger.CREDIT),0) > 0
	);

	UPDATE TrialBalTbl as tbal 
	SET L1CredSUM = (
			SELECT SUM(pledger.CREDIT) -SUM(pledger.DEBIT)
			FROM 
				pledger as pledger INNER JOIN party party ON pledger.pid = party.pid
						INNER JOIN level3 level3 ON level3.l3 = party.level3 
						INNER JOIN level2 level2 ON level3.l2 = level2.l2 
						INNER JOIN level1 level1 on level1.l1 = level2.l1
			WHERE 
				level1.l1 = tbal.l1 AND DATE(pledger.date) BETWEEN startDate AND endDate AND pledger.company_id=company_id
			GROUP BY  
				level1.L1
			HAVING 
				IFNULL(SUM(pledger.CREDIT),0)-IFNULL(SUM(pledger.DEBIT),0) > 0
	);

	UPDATE TrialBalTbl as tbal 
	SET L2DebSUM = (
			SELECT SUM(pledger.DEBIT)-SUM(pledger.CREDIT) 
			FROM 
				pledger pledger INNER JOIN party party ON pledger.pid = party.pid 
						INNER JOIN level3 level3 ON level3.l3 = party.level3 
						INNER JOIN level2 level2 ON level3.l2 = level2.l2 
						INNER JOIN level1 level1 on level1.l1 = level2.l1
			WHERE 
				level1.l1 = tbal.l1 AND level2.l2 = tbal.l2 AND DATE(pledger.date) BETWEEN startDate AND endDate AND pledger.company_id=company_id
			GROUP BY  
				level1.L1, level2.L2
			HAVING 
				IFNULL(SUM(pledger.DEBIT),0)-IFNULL(SUM(pledger.CREDIT),0) > 0
	);

	UPDATE TrialBalTbl as tbal 
	SET L2CredSUM = (
			SELECT SUM(pledger.CREDIT) -SUM(pledger.DEBIT)
			FROM 
				pledger pledger INNER JOIN party party ON pledger.pid = party.pid
						INNER JOIN level3 level3 ON level3.l3 = party.level3 
						INNER JOIN level2 level2 ON level3.l2 = level2.l2 
						INNER JOIN level1 level1 on level1.l1 = level2.l1
			WHERE 
				level1.l1 = tbal.l1 AND level2.l2 = tbal.l2 AND DATE(pledger.date) BETWEEN startDate AND endDate AND pledger.company_id=company_id
			GROUP BY  
				level1.L1, level2.L2
			HAVING 
				IFNULL(SUM(pledger.CREDIT),0)-IFNULL(SUM(pledger.DEBIT),0) > 0
	);

	UPDATE TrialBalTbl as tbal 
	SET L3DebSUM = (
			SELECT SUM(pledger.DEBIT)-SUM(pledger.CREDIT) 
			FROM 
				pledger pledger INNER JOIN party party ON pledger.pid = party.pid
						INNER JOIN level3 level3 ON level3.l3 = party.level3 
						INNER JOIN level2 level2 ON level3.l2 = level2.l2 
						INNER JOIN level1 level1 on level1.l1 = level2.l1
			WHERE 
				level1.l1 = tbal.l1 AND level2.l2 = tbal.l2 AND level3.l3 = tbal.l3 AND DATE(pledger.date) BETWEEN startDate AND endDate AND pledger.company_id=company_id
			GROUP BY  
				level1.L1, level2.L2, level3.L3
			HAVING 
				IFNULL(SUM(pledger.DEBIT),0)-IFNULL(SUM(pledger.CREDIT),0) > 0
	);

	UPDATE TrialBalTbl as tbal 
	SET L3CredSUM = (
			SELECT SUM(pledger.CREDIT) -SUM(pledger.DEBIT)
			FROM 
				pledger pledger INNER JOIN party party ON pledger.pid = party.pid
						INNER JOIN level3 level3 ON level3.l3 = party.level3 
						INNER JOIN level2 level2 ON level3.l2 = level2.l2 
						INNER JOIN level1 level1 on level1.l1 = level2.l1
			WHERE 
				level1.l1 = tbal.l1 AND level2.l2 = tbal.l2 AND level3.l3 = tbal.l3 AND DATE(pledger.date) BETWEEN startDate AND endDate AND pledger.company_id=company_id
			GROUP BY  
				level1.L1, level2.L2, level3.L3
			HAVING 
				IFNULL(SUM(pledger.CREDIT),0)-IFNULL(SUM(pledger.DEBIT),0) > 0
	);

	SET CredSUM = (SELECT SUM(DISTINCT L1CredSUM) FROM TrialBalTbl);
	SET DebSUM  = (SELECT SUM(DISTINCT L1DebSUM ) FROM TrialBalTbl);

	UPDATE TrialBalTbl SET
		totalCred = CredSUM,
		totalDeb  = DebSUM;

	if l3_p<>0 then
		SELECT * FROM TrialBalTbl where TrialBalTbl.l3=l3_p ORDER BY ACCOUNT_ID;
	elseif l2_p<>0 then
			SELECT * FROM TrialBalTbl where TrialBalTbl.l2=l2_p ORDER BY ACCOUNT_ID;
	elseif l1_p<>0 then
				SELECT * FROM TrialBalTbl where TrialBalTbl.l1=l1_p ORDER BY ACCOUNT_ID;
	else
					SELECT * FROM TrialBalTbl ORDER BY ACCOUNT_ID;
	end if;

END$$

CREATE DEFINER=`root`@`localhost` PROCEDURE `wagessheet` (IN `_from` VARCHAR(50), IN `_to` VARCHAR(50), IN `cid` INT)  BEGIN

	declare pdays int default 0;	
	declare sal_cal varchar(10);
	declare daystotal int default 0;
    declare monthLastDay datetime;
	
	SELECT 
    sal_cal
INTO sal_cal FROM
    setting
LIMIT 1;
	
	if sal_cal = '30days' then
		set daystotal = 30;
	else
		SELECT DAY(LAST_DAY(_to)) INTO daystotal;
	end if;
	
	drop table if exists basicstaffinf;
	drop table if exists atndInf;
	drop table if exists advance;
	drop table if exists loan;
	drop table if exists penalty;
	drop table if exists incentive;
	drop table if exists hourswork;

	create temporary table if not exists basicstaffinf (
		id int not null primary key auto_increment,
		staid int,
		did int,
		pid int,
		name varchar(500),
		fname varchar(500),
		department_name varchar(500),
		designation varchar(500),
		bsalary decimal(10, 4),
		otrate decimal(10, 4),
		eobi decimal(10, 4),
		insurance decimal(10, 4), 
		socialsec decimal(10, 4),
		shid int
	);
	
	truncate table basicstaffinf;
	
	insert into basicstaffinf (staid, name, fname, designation, did, bsalary, pid, department_name, otrate, eobi, insurance, socialsec, shid) SELECT stf.staid, stf.name, stf.fname, stf.type, stf.did, sal.totalpay, stf.pid, d.name AS 'department_name', stf.otrate, sal.eobi, sal.insurance, sal.socialsec, s.shid FROM staff AS stf INNER JOIN salary AS sal ON stf.staid = sal.staid INNER JOIN department AS d ON stf.did = d.did INNER JOIN allot_shift AS _as ON _as.gid = stf.gid INNER JOIN shift AS s ON s.shid = _as.shid WHERE stf.active = 1 and stf.`type` = 'contract' and stf.company_id=cid;	

	create temporary table if not exists atndInf (
		id int not null primary key auto_increment,
		staid int,
		paid_days int,
		present int,
		absent int,
		paidleave int,
		unpaidleave int,
		restday int,
		gustedholiday int,
		shortleave int,
		outdoor int
	);	
	
	truncate table atndInf;
	
	insert into atndInf (staid,paid_days,present,absent,paidleave,unpaidleave,restday,gustedholiday,shortleave,outdoor)	
	select staid,
		daystotal as 'paid_days',
		count(case when status = 'Present' THEN staid END) as 'present',
		count(case when status = 'Absent' THEN staid END) as 'absent',
		count(case when status = 'Paid Leave' THEN staid END) as 'paidleave',
		count(case when status = 'Unpaid Leave' THEN staid END) as 'unpaidleave',
		count(case when status = 'Rest Day' THEN staid END) as 'restday',
		count(case when status = 'Gusted Holiday' THEN staid END) as 'gustedholiday',
		count(case when status = 'Short Leave' THEN staid END) as 'shortleave',
		count(case when status = 'Outdoor' THEN staid END) as 'outdoor'		
	from staffatndetail 
	where date >= _from and date <= _to and etype = 'vr_atnd'
	GROUP BY staid;
	
   -- for advance
	CREATE TABLE IF NOT EXISTS advance (
    id INT NOT NULL PRIMARY KEY AUTO_INCREMENT,
    pid INT,
    adv DECIMAL(10 , 4 )
);
	truncate table advance;
	insert into advance(pid, adv) select pid, sum(debit) as adv from pledger where etype = 'advance' and date >= _from and date <= _to group by pid;
	
	-- for penalty
	CREATE TABLE IF NOT EXISTS penalty (
    id INT NOT NULL PRIMARY KEY AUTO_INCREMENT,
    pid INT,
    pen DECIMAL(10 , 4 )
);
	truncate table penalty;
	insert into penalty(pid, pen) select pid, sum(debit) as pen from pledger where etype = 'penalty' and date >= _from and date <= _to group by pid;
	
	-- for incentive
	create temporary table if not exists incentive(
		id int not null primary key AUTO_INCREMENT,
		pid int,
		inc decimal(10, 4)
	);
	truncate table incentive;
	insert into incentive(pid, inc) select pid, sum(credit) as inc from pledger where etype = 'incentive' and date >= _from and date <= _to group by pid;	
	
	-- for loan
	create temporary table if not exists loan(
		id int not null primary key AUTO_INCREMENT,
		pid int,
		lon decimal(10, 4),
		deduction decimal(10, 4),
		balance decimal(10, 4)
	);
	truncate table loan;
create temporary table if not exists loan(
		id int not null primary key AUTO_INCREMENT,
		pid int,
		lon decimal(10, 4),
		deduction decimal(10, 4),
		balance decimal(10, 4)
	);
	truncate table loan;
insert into loan(pid, lon, deduction, balance) SELECT pl.pid, IFNULL(SUM(pl.debit),0)- IFNULL(SUM(pl.credit),0) AS 'lon', IF((IFNULL(SUM(pl.debit),0)- IFNULL(SUM(pl.credit),0)-ifnull(adv.adv,0))> IFNULL(ded.deduction,0), IFNULL(ded.deduction,0),(IFNULL(SUM(pl.debit),0)- IFNULL(SUM(pl.credit),0)-ifnull(adv.adv,0))) AS deduction,(IFNULL(SUM(pl.debit),0)- IFNULL(SUM(pl.credit),0)-ifnull(adv.adv,0)) - IF((IFNULL(SUM(pl.debit),0)- IFNULL(SUM(pl.credit),0)-ifnull(adv.adv,0))> IFNULL(ded.deduction,0), IFNULL(ded.deduction,0),(IFNULL(SUM(pl.debit),0)- IFNULL(SUM(pl.credit),0)-ifnull(adv.adv,0))) AS balance
FROM pledger pl
LEFT JOIN (
SELECT l.pid,(l.deduction) AS deduction
FROM pledger l
WHERE l.pledid=(
SELECT pledid
FROM pledger
WHERE pid=l.pid AND etype='loan' AND DATE<=_to
ORDER BY DATE DESC
LIMIT 1) AND l.etype='loan'
GROUP BY l.pid
) as ded on ded.pid=pl.pid
left join(
SELECT pid, IFNULL(SUM(debit),0)- IFNULL(SUM(credit),0) AS 'adv'
from pledger where etype in ('advance','incentive','penalty') AND DATE>=_from and  DATE<=_to
group by pid
) as adv on adv.pid=pl.pid

WHERE  pl.date<=_to  and pl.pid in (select pid from party where level3 in (select l3 from level3 where name like 'asset employeed' ))
GROUP BY pl.pid;

	-- for overtime
	create temporary table if not exists hourswork(
		id int not null primary key AUTO_INCREMENT,
		staid int,
		othour decimal(10, 2)
	);
	truncate table hourswork;
	insert into hourswork(staid, othour) SELECT staid, SUM(othour) AS othour FROM overtime WHERE DATE >= _from AND DATE <= _to GROUP BY staid;

	SELECT 
    b.staid,
    b.did,
    b.department_name,
    b.pid,
    b.shid,
    b.name,
    b.fname,
    b.designation,
    IFNULL(ROUND(b.bsalary, 2), 0) AS 'bsalary',
    IFNULL(a.absent, 0) as absent,
    IFNULL(paidleave, 0) AS 'leave_wp',
    IFNULL(a.unpaidleave, 0) AS 'leave_wop',
    IFNULL(a.gustedholiday, 0) AS 'gusted_holiday',
    IFNULL(a.outdoor, 0) AS 'outdoor',
    IFNULL((a.shortleave / 2), 0) AS 'short_leave',
    IFNULL(a.restday, 0) AS 'rest_days',
    IFNULL(a.present, 0) AS 'work_days',
    IFNULL((a.present + a.restday + paidleave + a.gustedholiday + a.outdoor + (a.shortleave / 2)), 0) AS 'paid_days',
    round(ifnull(wag.amount,0),0) AS 'gross_salary',
    ROUND(b.otrate, 2) AS otrate,
    IFNULL(hw.othour, 0) AS othour,
    ROUND(IFNULL((b.otrate * hw.othour), 0), 2) AS overtime,
    ROUND(IFNULL(ad.adv, 0), 2) AS 'advance',
    ROUND(IFNULL(l.deduction, 0), 2) AS 'loan_deduction',
    ROUND(IFNULL(l.balance, 0), 2) AS 'balance',
    ROUND(IFNULL(inc.inc, 0), 2) AS 'incentive',
    ROUND(IFNULL(p.pen, 0), 2) AS 'penalty',
    ROUND(IFNULL(b.eobi, 0), 2) AS 'eobi',
    ROUND(IFNULL(b.insurance, 0), 2) AS 'insurance',
    ROUND(IFNULL(b.socialsec, 0), 2) AS 'socialsec',
    ROUND((ifnull(wag.amount,0) + IFNULL((b.otrate * hw.othour), 0) + IFNULL(inc.inc, 0) - IFNULL(ad.adv, 0) - IFNULL(l.deduction, 0) - IFNULL(b.eobi, 0) - IFNULL(b.insurance, 0) - IFNULL(b.socialsec, 0) - IFNULL(p.pen, 0)),
            2) AS 'net_salary'
FROM
    basicstaffinf AS b
        LEFT OUTER JOIN
    atndInf AS a ON b.staid = a.staid
        LEFT OUTER JOIN
    advance AS ad ON b.pid = ad.pid
        LEFT OUTER JOIN
    penalty AS p ON b.pid = p.pid
        LEFT OUTER JOIN
    loan AS l ON b.pid = l.pid
        LEFT OUTER JOIN
    incentive AS inc ON b.pid = inc.pid
        LEFT OUTER JOIN
    hourswork AS hw ON b.staid = hw.staid
	 left join(
	 SELECT ifnull(sum(d.lamount),0) as amount,d.emp_id
				FROM stockmain AS m 
				INNER JOIN stockdetail AS d ON m.stid = d.stid 
				WHERE m.vrdate >= _from AND m.vrdate <= _to and m.company_id = cid AND m.etype = 'production' and d.type='produce'
				group by d.emp_id
	 ) as wag on wag.emp_id=b.staid
	 ;
END$$

