CREATE TRIGGER `stockdetail_after_insert` AFTER INSERT ON `stockdetail` FOR EACH ROW BEGIN
DECLARE oldItem_id VARCHAR(16); 

DECLARE oldEtype VARCHAR(50) DEFAULT ''; 

DECLARE avgratei DECIMAL(18,2); 
DECLARE oldAvg_rate DECIMAL(18,2); 

DECLARE stock_qty DECIMAL(18,2); 
DECLARE net_amount DECIMAL(18,2); 
DECLARE qty_net DECIMAL(18,2); 

DECLARE last_prate_new DECIMAL(18,2); 

SET net_amount=0; 
SET stock_qty=0; 
SET qty_net=0; 


SET avgratei=0; 
SET oldAvg_rate=0;

SET last_prate_new=0;

SELECT stockdetail.item_id,stockmain.etype,IFNULL(stockdetail.qty,0),IFNULL(stockdetail.netamount,0),IFNULL(stockdetail.netamount,0)/IFNULL(stockdetail.qty,0)  INTO oldItem_id,oldEtype,qty_net,net_amount,last_prate_new
FROM stockdetail 
inner join stockmain on stockmain.stid = stockdetail.stid
WHERE stdid=new.stdid;


SELECT ifnull(item.qty,0) * IFNULL(item.avg_rate,0) ,ifnull(item.qty,0) INTO avgratei,stock_qty
FROM item 
WHERE item_id=oldItem_id ;

IF(qty_net >0  AND net_amount>0 AND (oldEtype='production' OR oldEtype='materialreturn' OR oldEtype='frv') ) THEN
	SET oldAvg_rate=0;
	
	if (avgratei + net_amount <>0 AND qty_net + stock_qty <>0 ) then
	
	SET oldAvg_rate= ((avgratei + net_amount) / (qty_net + stock_qty)) ;
	
	END if;
	
	
	  UPDATE item SET qty= qty_net + stock_qty,avg_rate = oldAvg_rate
	   WHERE item_id=oldItem_id;

ELSEIF(qty_net <0  ) THEN
	
	

	  UPDATE item SET qty=ifnull(stock_qty,0) + ifnull(qty_net,0)
	   WHERE item_id=oldItem_id ;
	


END IF;





END
$$
